import {useState} from 'react'
const CallApi = () =>{
  const [input, setInput] = useState('')
  const [data, setData] = useState([])
  const fetchApi = async () =>{
    alert('hii')
    const url = 'https://nepal-weather-api.herokuapp.com/api/?place=all'
    const res = await fetch(url)
    const fetchdata = await res.json()
    alert(fetchdata)
    setData(fetchdata)
  }

  return(
    <>
    <h1> Live search weather App </h1>
    <button onClick={fetchApi}> get data </button>
    <input onChange={(e) => setInput(e.target.value)} placeholder='enter city name ...'/>
    {
      data.map((curr) =>{
        return <h1> hii {curr}</h1>
      })
    }
    </>
  )
}
export default CallApi
