import {useState} from 'react'
const CallApi = () =>{
  const [input, setInput] = useState('')
  const [data, setData] = useState([])
  const fetchApi = async () =>{
    alert('hii')
    const url = `https://api.openweathermap.org/data/2.5/weather?q=${input}&units=metric&appid=862b05f14f00d218712da3bba053bd10`
    const res = await fetch(url)
    alert(res)
    const fetchdata = await res.json()
    alert(fetchdata)

    setData(fetchdata.main)
  }

  return(
    <>
    {!data ? 
    <p> data not found </p> :
    <>
    <h1> Live search weather App </h1>
    <button onClick={fetchApi}> get data </button>
    <input onChange={(e) => setInput(e.target.value)} placeholder='enter city name ...'/>
    <h1> hii {data.temp} </h1>
    <h1> hii {input} </h1>
    </>
    }
    </>
  )
}
export default CallApi
