import {useState} from 'react'
import {Card, TextField} from '@mui/material'
import './CallApi.css'
const CallApi = () =>{
  const [input, setInput] = useState('')
  const [data, setData] = useState([])
  const fetchApi = async () =>{
    alert('hii')
    const url = `https://api.openweathermap.org/data/2.5/weather?q=${input}&units=metric&appid=862b05f14f00d218712da3bba053bd10`
    const res = await fetch(url)
    alert(res)
    const fetchdata = await res.json()
    alert(fetchdata)

    setData(fetchdata.main)
  }

  return(
    <>
    <Card variant='outlined' className='card'>
      <h1 className='title'> Live search weather App </h1>
      <TextField onChange={(e) => setInput(e.target.value)} lab='enter city name ' variant='outlined'/>
      <button onClick={fetchApi}> get data </button>
      <h1> hii {!data ? 'data not found' : data.temp} </h1>
      <h1> hii {input} </h1>
    </Card>
    }
    </>
  )
}
export default CallApi
